import 'dart:convert';
import 'dart:typed_data';

import 'package:flutter/material.dart';

import 'blob_utils.dart';

const simpleBase64Image =
    'data:image/gif;base64,';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Blob Demo',
      theme: ThemeData.dark(),
      home: MyHomePage(title: 'Blob example'),
    );
  }
}

class MyHomePage extends StatefulWidget {
  MyHomePage({Key key, this.title}) : super(key: key);

  final String title;

  @override
  _MyHomePageState createState() => _MyHomePageState();
}

class _MyHomePageState extends State<MyHomePage> {
  String url;
  Uint8List bytes;

  void loadImages() async {
    final blob = await convertBase64ToBlob(simpleBase64Image);
    url = convertBlobToUrl(blob);
    print('URL = $url');
    try {
      bytes = await convertBlobUrlToBytes(url);
    } catch (e) {
      print('cannot convert to bytes $e');
    }
    setState(() {});
  }

  @override
  void initState() {
    super.initState();
    loadImages();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text(widget.title),
      ),
      body: Center(
        child: Column(
          // crossAxisAlignment: ,
          mainAxisAlignment: MainAxisAlignment.center,
          children: <Widget>[
            ...[
              Text('Image from base 64'),
              Image.memory(
                base64Decode(
                  simpleBase64Image.split(',').last,
                ),
              )
            ],
            const SizedBox(height: 50),
            if (url != null) ...[
              Text(
                'Image From URL \n$url',
                textAlign: TextAlign.center,
              ),
              const SizedBox(height: 10),
              Image.network(url),
            ],
            const SizedBox(height: 50),
            if (bytes != null) ...[
              Text('Image from memory:'),
              const SizedBox(height: 10),
              Image.memory(bytes),
            ],
          ],
        ),
      ),
    );
  }
}
